import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import cyberEarthImage from '../images/cyberearth.png';
import { apiPost, getToken } from '../utils/api';
import { mockSimulationResponse } from '../data/mockSimulationData';
import InputPanel from '../components/simulate/InputPanel';
import SummaryCards from '../components/simulate/SummaryCards';
import TimeSeriesChart from '../components/simulate/TimeSeriesChart';
import EnergyMixChart from '../components/simulate/EnergyMixChart';
import CO2DonutChart from '../components/simulate/CO2DonutChart';
import SimilarPoliciesPanel from '../components/simulate/SimilarPoliciesPanel';
import DisclaimerBox from '../components/simulate/DisclaimerBox';

function Simulate() {
  const navigate = useNavigate();
  const [inputs, setInputs] = useState({
    country: '',
    policyType: 'CARBON_TAX',
    carbonPriceUSD: '',
    durationYears: 5
  });
  const [results, setResults] = useState(null);
  const [loading, setLoading] = useState(false);
  const [errors, setErrors] = useState({});
  const [apiError, setApiError] = useState(null);
  const [useMockData, setUseMockData] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem('token');
    setIsLoggedIn(!!token);
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setInputs(prev => ({
      ...prev,
      [name]: value
    }));
    // Clear errors when user types
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
    setApiError(null);
  };

  const validateInputs = () => {
    const newErrors = {};
    
    if (!inputs.country || inputs.country.trim() === '') {
      newErrors.country = 'Country is required';
    }
    
    if (!inputs.carbonPriceUSD || inputs.carbonPriceUSD <= 0) {
      newErrors.carbonPriceUSD = 'Carbon price must be greater than 0';
    }
    
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async () => {
    // Check if user is logged in
    if (!isLoggedIn) {
      navigate('/signup');
      return;
    }

    if (!validateInputs()) {
      return;
    }

    setLoading(true);
    setApiError(null);
    setResults(null);

    try {
      // Prepare request body matching API contract
      const requestBody = {
        country: inputs.country,
        policyType: inputs.policyType,
        carbonPriceUSD: parseFloat(inputs.carbonPriceUSD),
        durationYears: parseInt(inputs.durationYears)
      };

      const response = await fetch(`${API_BASE_URL}/simulate`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify(requestBody)
      });

      const data = await response.json();

      if (!response.ok || !data.success) {
        // If API fails, use mock data
        console.warn('API request failed, using mock data:', data.message || 'Unknown error');
        setUseMockData(true);
        setResults(mockSimulationResponse);
      } else {
        setUseMockData(false);
        // Transform backend response to match our expected format
        const transformedData = transformApiResponse(data);
        setResults(transformedData);
      }
    } catch (error) {
      console.error('Simulation error:', error);
      // On network error, use mock data
      setUseMockData(true);
      setApiError('Cannot connect to server. Using mock data for demonstration.');
      setResults(mockSimulationResponse);
    } finally {
      setLoading(false);
    }
  };

  const transformApiResponse = (apiData) => {
    // Backend should already return the correct format, but ensure it matches
    return {
      inputs: apiData.inputs || {
        country: inputs.country,
        policyType: inputs.policyType,
        carbonPriceUSD: inputs.carbonPriceUSD,
        durationYears: inputs.durationYears
      },
      predictions: apiData.predictions || {
        emissionCoveragePct: 0,
        annualRevenueMillionUSD: 0
      },
      countryContext: apiData.countryContext || {},
      similarPolicies: apiData.similarPolicies || []
    };
  };

  return (
    <div className="page-container simulate-page">
      <div className="page-background">
        <div className="cyber-earth-container">
          <img src={cyberEarthImage} alt="Cyber Earth" className="cyber-earth" />
        </div>
      </div>
      <div className="page-content-wrapper">
        <div className="page-content">
          <InputPanel
            inputs={inputs}
            onInputChange={handleInputChange}
            onSubmit={handleSubmit}
            loading={loading}
            errors={errors}
          />

          {apiError && (
            <div className="api-error-notice">
              <p>{apiError}</p>
            </div>
          )}

          {useMockData && (
            <div className="mock-data-notice">
              <p>⚠️ Using mock data for demonstration. Connect to backend API for real predictions.</p>
            </div>
          )}

          {results && (
            <>
              <div className="results-section">
                <h2 className="section-title">Simulation Results</h2>
                
                <SummaryCards
                  predictions={results.predictions}
                  countryContext={results.countryContext}
                />

                <DisclaimerBox />

                <div className="charts-grid">
                  <div className="chart-wrapper">
                    <TimeSeriesChart
                      durationYears={results.inputs.durationYears}
                      predictions={results.predictions}
                      yearStart={2024}
                    />
                  </div>

                  <div className="chart-wrapper">
                    <EnergyMixChart countryContext={results.countryContext} />
                  </div>

                  <div className="chart-wrapper">
                    <CO2DonutChart
                      predictions={results.predictions}
                      countryContext={results.countryContext}
                    />
                  </div>
                </div>

                {results.similarPolicies && results.similarPolicies.length > 0 && (
                  <div className="similar-policies-wrapper">
                    <SimilarPoliciesPanel similarPolicies={results.similarPolicies} />
                  </div>
                )}
              </div>
            </>
          )}

          {!results && (
            <div className="page-description">
              <p>
                Configure key policy settings, duration, carbon price, and region. 
                Generate real-time projections of how your climate strategy will affect both the environment and revenue.
              </p>
              <p>
                After running the simulation, Eco-Impact AI displays clear insights on emission coverage, 
                revenue predictions, and CO₂ coverage, supported by interactive charts for deeper analysis.
              </p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default Simulate;
