import React from 'react';
import TimeSeriesChart from '../simulate/TimeSeriesChart';
import EnergyMixChart from '../simulate/EnergyMixChart';
import FossilFuelTimeSeriesChart from '../simulate/FossilFuelTimeSeriesChart';
import CO2DonutChart from '../simulate/CO2DonutChart';

function CompareGraphs({ simulations, onBack }) {
  if (!simulations || simulations.length === 0) {
    return (
      <div className="compare-graphs-empty">
        <p>No simulations selected for comparison.</p>
        {onBack && (
          <button 
            className="back-to-compare-btn"
            onClick={onBack}
          >
            Back to Table
          </button>
        )}
      </div>
    );
  }

  return (
    <div className="compare-graphs-container">
      <div className="compare-graphs-header">
        <h2 className="compare-graphs-title">Comparative Analysis ({simulations.length} policies)</h2>
        {onBack && (
          <button 
            className="back-to-compare-btn"
            onClick={onBack}
          >
            Back to Table
          </button>
        )}
      </div>

      <div className="compare-graphs-grid">
        {simulations.map((simulationResult, index) => {
          const { predictions, countryContext, inputs, fossilFuelTimeSeries } = simulationResult;
          const country = inputs?.country || 'Unknown';
          const policyType = inputs?.policyType === 'CARBON_TAX' ? 'Carbon Tax' : 'ETS';
          const carbonPrice = inputs?.carbonPriceUSD || 0;
          const duration = inputs?.durationYears || 0;

          return (
            <div key={simulationResult.id || index} className="compare-graph-item">
              <div className="compare-graph-header">
                <h3 className="compare-policy-title">
                  Policy {index + 1}: {country} - {policyType}
                </h3>
                <div className="compare-policy-details">
                  <span>Price: ${carbonPrice.toFixed(2)}/ton COâ‚‚</span>
                  <span>Duration: {duration} years</span>
                </div>
              </div>

              <div className="compare-charts-wrapper">
                <div className="compare-chart-item">
                  <TimeSeriesChart
                    durationYears={duration}
                    predictions={predictions}
                    yearStart={2024}
                  />
                </div>

                <div className="compare-chart-item">
                  <EnergyMixChart countryContext={countryContext} />
                </div>

                <div className="compare-chart-item">
                  <FossilFuelTimeSeriesChart 
                    fossilFuelTimeSeries={fossilFuelTimeSeries || null} 
                  />
                </div>

                <div className="compare-chart-item">
                  <CO2DonutChart
                    predictions={predictions}
                    countryContext={countryContext}
                  />
                </div>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default CompareGraphs;
