import React from 'react';

function SummaryCards({ predictions, countryContext, success }) {
  // Calculate metrics
  const emissionCoveragePct = predictions?.emissionCoveragePct || 0;
  const annualRevenueMillionUSD = predictions?.annualRevenueMillionUSD || 0;
  
  // CO₂ Covered calculation
  // Use backend-calculated value if available (from yearly_predictions), otherwise calculate
  const annualCO2Tons = countryContext?.annualCO2Tons || null;
  let co2CoveredMillionTons = null;
  if (annualCO2Tons && annualCO2Tons > 0) {
    const co2CoveredTons = (emissionCoveragePct / 100) * annualCO2Tons;
    co2CoveredMillionTons = co2CoveredTons / 1000000;
  }
  
  // Revenue as % of GDP
  // Use backend-calculated value if available, otherwise calculate
  const revenueUSD = annualRevenueMillionUSD * 1000000;
  const gdpUSD = countryContext?.gdpUSD || countryContext?.gdpPPP || null;
  let revenuePctGDP = null;
  if (gdpUSD && gdpUSD > 0) {
    revenuePctGDP = (revenueUSD / gdpUSD) * 100;
  }

  const formatNumber = (num, decimals = 1) => {
    return new Intl.NumberFormat('en-US', {
      minimumFractionDigits: decimals,
      maximumFractionDigits: decimals
    }).format(num);
  };

  const formatCurrency = (num) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 1,
      maximumFractionDigits: 1,
      notation: 'compact',
      compactDisplay: 'short'
    }).format(num);
  };

  const cards = [
    {
      title: 'Emission Coverage',
      value: `${formatNumber(emissionCoveragePct, 1)}%`,
      subtitle: 'ML Prediction',
      color: '#00FF6F'
    },
    {
      title: 'Annual Revenue',
      value: formatCurrency(annualRevenueMillionUSD * 1000000),
      subtitle: 'ML Prediction',
      color: '#01D6DF'
    },
    {
      title: 'CO₂ Covered',
      value: co2CoveredMillionTons !== null && co2CoveredMillionTons > 0 
        ? `${formatNumber(co2CoveredMillionTons, 2)}M tons/year`
        : 'N/A',
      subtitle: 'Calculated',
      color: '#00FF6F'
    },
    {
      title: 'Revenue as % of GDP',
      value: revenuePctGDP !== null && revenuePctGDP > 0 && revenuePctGDP < 100
        ? `${formatNumber(revenuePctGDP, 2)}%`
        : 'N/A',
      subtitle: 'Calculated',
      color: '#01D6DF'
    }
  ];

  return (
    <div className="summary-cards">
      {cards.map((card, index) => (
        <div key={index} className="summary-card">
          <div className="summary-card-header">
            <h3 className="summary-card-title">{card.title}</h3>
            <span className="summary-card-subtitle">{card.subtitle}</span>
          </div>
          <div className="summary-card-value" style={{ color: card.color }}>
            {card.value}
          </div>
        </div>
      ))}
    </div>
  );
}

export default SummaryCards;
