import React from 'react';
import { useNavigate } from 'react-router-dom';
import TimeSeriesChart from '../simulate/TimeSeriesChart';
import EnergyMixChart from '../simulate/EnergyMixChart';
import FossilFuelTimeSeriesChart from '../simulate/FossilFuelTimeSeriesChart';
import CO2DonutChart from '../simulate/CO2DonutChart';
import { formatSimulationForComparison } from '../../utils/simulationStorage';

function CompareGraphs({ simulations }) {
  const navigate = useNavigate();

  if (!simulations || simulations.length === 0) {
    return (
      <div className="compare-graphs-empty">
        <p>No simulations selected for comparison.</p>
      </div>
    );
  }

  const formattedSimulations = simulations.map(sim => formatSimulationForComparison(sim));

  return (
    <div className="compare-graphs-container">
      <div className="compare-graphs-header">
        <h2 className="compare-graphs-title">Comparative Analysis</h2>
        <button 
          className="back-to-compare-btn"
          onClick={() => navigate('/compare')}
        >
          Back to Compare
        </button>
      </div>

      <div className="compare-graphs-grid">
        {formattedSimulations.map((sim, index) => {
          // Reconstruct the simulation result format for charts
          const simulationResult = simulations[index];
          const { predictions, countryContext, inputs } = simulationResult;

          return (
            <div key={sim.id || index} className="compare-graph-item">
              <div className="compare-graph-header">
                <h3 className="compare-policy-title">
                  Policy {index + 1}: {sim.country} - {sim.policyType}
                </h3>
                <div className="compare-policy-details">
                  <span>Price: ${sim.carbonPrice}/ton COâ‚‚</span>
                  <span>Duration: {sim.duration} years</span>
                </div>
              </div>

              <div className="compare-charts-wrapper">
                <div className="compare-chart-item">
                  <TimeSeriesChart
                    durationYears={inputs?.durationYears || sim.duration}
                    predictions={predictions}
                    yearStart={2024}
                  />
                </div>

                <div className="compare-chart-item">
                  <EnergyMixChart countryContext={countryContext} />
                </div>

                <div className="compare-chart-item">
                  <FossilFuelTimeSeriesChart 
                    fossilFuelTimeSeries={simulationResult.fossilFuelTimeSeries || null} 
                  />
                </div>

                <div className="compare-chart-item">
                  <CO2DonutChart
                    predictions={predictions}
                    countryContext={countryContext}
                  />
                </div>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default CompareGraphs;
