import React, { useEffect, useState } from 'react';
import { useLocation, Routes, Route } from 'react-router-dom';
import Home from '../pages/Home';
import Signup from '../pages/Signup';
import Login from '../pages/Login';
import Simulate from '../pages/Simulate';
import Compare from '../pages/Compare';
import SimulateResults from '../pages/SimulateResults';
import CompareResults from '../pages/CompareResults';

function AnimatedRoutes() {
  const location = useLocation();
  const [displayLocation, setDisplayLocation] = useState(location);
  const [transitionStage, setTransitionStage] = useState('enter');

  useEffect(() => {
    if (location !== displayLocation) {
      setTransitionStage('exit');
    }
  }, [location, displayLocation]);

  const onExited = () => {
    setDisplayLocation(location);
    setTransitionStage('enter');
  };

  // Handle transition end
  useEffect(() => {
    if (transitionStage === 'exit') {
      const timer = setTimeout(() => {
        onExited();
      }, 300); // Match animation duration
      return () => clearTimeout(timer);
    }
  }, [transitionStage]);

  return (
    <div 
      className={`route-transition route-transition-${transitionStage}`}
      onAnimationEnd={() => {
        if (transitionStage === 'exit') {
          onExited();
        }
      }}
    >
      <Routes location={displayLocation}>
        <Route path="/" element={<Home />} />
        <Route path="/signup" element={<Signup />} />
        <Route path="/login" element={<Login />} />
        <Route path="/simulate" element={<Simulate />} />
        <Route path="/compare" element={<Compare />} />
        <Route path="/simulate/results" element={<SimulateResults />} />
        <Route path="/compare/results" element={<CompareResults />} />
      </Routes>
    </div>
  );
}

export default AnimatedRoutes;
