import React, { useState } from 'react';
import { formatSimulationForComparison } from '../../utils/simulationStorage';
import ExportMenu from './ExportMenu';

function CompareTable({ simulations, selectedSimulationIds = [], onCheckboxChange, onRemove, onClearAll }) {
  const [showExportMenu, setShowExportMenu] = useState(false);

  // Get selected simulations for export
  const selectedSimulations = simulations.filter(sim => selectedSimulationIds.includes(sim.id));

  if (!simulations || simulations.length === 0) {
    return (
      <div className="compare-table-empty">
        <p>No simulations to compare yet.</p>
        <p>Run simulations on the Simulate page to see them here.</p>
      </div>
    );
  }

  // Format simulations for display
  const formattedSimulations = simulations.map(sim => formatSimulationForComparison(sim));

  // Find best values for highlighting (column-wise, with proper NaN/null handling)
  const validRevenues = formattedSimulations.map(s => s.annualRevenue).filter(v => v != null && !isNaN(v) && v > 0);
  const validCO2Covered = formattedSimulations.map(s => s.co2Covered).filter(v => v != null && !isNaN(v) && v > 0);
  const validRevenuePctGDP = formattedSimulations.map(s => s.revenuePctGDP).filter(v => v != null && !isNaN(v) && v > 0);
  const validSuccessProb = formattedSimulations.map(s => s.successProbability).filter(v => v != null && !isNaN(v) && v >= 0);

  const maxRevenue = validRevenues.length > 0 ? Math.max(...validRevenues) : null;
  const maxCO2Covered = validCO2Covered.length > 0 ? Math.max(...validCO2Covered) : null;
  const maxRevenuePctGDP = validRevenuePctGDP.length > 0 ? Math.max(...validRevenuePctGDP) : null;
  const maxSuccessProb = validSuccessProb.length > 0 ? Math.max(...validSuccessProb) : null;

  // Helper function to compare floating point numbers with tolerance
  const isEqual = (a, b, tolerance = 0.001) => {
    if (a == null || b == null || isNaN(a) || isNaN(b)) return false;
    return Math.abs(a - b) < tolerance;
  };

  const formatNumber = (num, decimals = 2) => {
    if (num === null || num === undefined || isNaN(num)) return 'N/A';
    return Number(num).toFixed(decimals);
  };

  return (
    <div className="compare-table-container">
      <div className="compare-table-header">
        <h2 className="compare-table-title">Policy Comparison</h2>
        <div className="compare-table-actions">
          <div className="export-btn-wrapper">
            <button 
              className="export-btn" 
              onClick={() => setShowExportMenu(!showExportMenu)}
              title="Download Options"
            >
              Download ▼
            </button>
            {showExportMenu && (
              <ExportMenu 
                data={selectedSimulationIds.length > 0 
                  ? formattedSimulations.filter(sim => selectedSimulationIds.includes(sim.id))
                  : formattedSimulations
                } 
                onClose={() => setShowExportMenu(false)}
              />
            )}
          </div>
          <button 
            className="clear-all-btn" 
            onClick={onClearAll}
            disabled={simulations.length === 0}
          >
            Clear All
          </button>
        </div>
      </div>

      <div className="compare-table-wrapper">
        <table className="compare-table">
          <thead>
            <tr>
              <th></th>
              <th>Country</th>
              <th>Policy Type</th>
              <th>Carbon Price<br/>(USD/ton CO₂)</th>
              <th>Duration<br/>(Years)</th>
              <th>Annual Revenue<br/>(Million USD)</th>
              <th>Policy Success<br/>(% / Risk)</th>
              <th>CO₂ Covered<br/>(Million tons/year)</th>
              <th>Revenue as %<br/>of GDP</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {formattedSimulations.map((sim, index) => {
              const isSelected = selectedSimulationIds.includes(sim.id);
              return (
              <tr key={sim.id || index} style={{ cursor: 'default' }}>
                <td>
                  <input
                    type="checkbox"
                    checked={isSelected}
                    onChange={(e) => {
                      e.stopPropagation();
                      onCheckboxChange(sim.id, e.target.checked);
                    }}
                    onClick={(e) => e.stopPropagation()}
                    style={{ cursor: 'pointer' }}
                  />
                </td>
                <td>{sim.country || '-'}</td>
                <td>{sim.policyType || '-'}</td>
                <td>{formatNumber(sim.carbonPrice, 2)}</td>
                <td>{sim.duration || 0}</td>
                <td 
                  className={
                    maxRevenue != null && 
                    isEqual(sim.annualRevenue, maxRevenue)
                      ? 'best-value' 
                      : ''
                  }
                >
                  {formatNumber(sim.annualRevenue, 2)}
                </td>
                <td 
                  className={
                    maxSuccessProb != null && 
                    sim.successProbability != null &&
                    isEqual(sim.successProbability, maxSuccessProb)
                      ? 'best-value' 
                      : ''
                  }
                >
                  {sim.successProbability !== null && sim.successProbability !== undefined
                    ? `${formatNumber(sim.successProbability * 100, 1)}% (${sim.status || 'N/A'})`
                    : 'N/A'}
                </td>
                <td 
                  className={
                    maxCO2Covered != null && 
                    sim.co2Covered != null &&
                    isEqual(sim.co2Covered, maxCO2Covered)
                      ? 'best-value' 
                      : ''
                  }
                >
                  {sim.co2Covered !== null ? formatNumber(sim.co2Covered, 2) : 'N/A'}
                </td>
                <td 
                  className={
                    maxRevenuePctGDP != null && 
                    sim.revenuePctGDP != null &&
                    isEqual(sim.revenuePctGDP, maxRevenuePctGDP)
                      ? 'best-value' 
                      : ''
                  }
                >
                  {sim.revenuePctGDP !== null ? formatNumber(sim.revenuePctGDP, 2) : 'N/A'}
                </td>
                <td>
                  <button 
                    className="remove-btn"
                    onClick={(e) => {
                      e.stopPropagation();
                      onRemove(sim.id);
                    }}
                    title="Remove this simulation"
                  >
                    Remove
                  </button>
                </td>
              </tr>
            )})}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default CompareTable;
