import React from 'react';
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer
} from 'recharts';

function FossilFuelTimeSeriesChart({ fossilFuelTimeSeries }) {
  if (!fossilFuelTimeSeries || fossilFuelTimeSeries.length === 0) {
    return (
      <div className="chart-container">
        <h3 className="chart-title">Projected Fossil Fuel Dependency</h3>
        <p style={{ textAlign: 'center', color: '#888', padding: '40px' }}>
          Fossil fuel dependency projection not available
        </p>
      </div>
    );
  }

  // Transform data for chart
  const chartData = fossilFuelTimeSeries.map(item => ({
    year: item.year,
    'Fossil Fuel Dependency': item.fossil_pct,
    isBaseline: item.is_ml_baseline || false
  }));

  const CustomTooltip = ({ active, payload }) => {
    if (active && payload && payload.length) {
      const data = payload[0].payload;
      return (
        <div className="chart-tooltip">
          <p className="chart-tooltip-label">{`Year: ${data.year}`}</p>
          <p style={{ color: payload[0].color }}>
            {`Fossil Fuel Dependency: ${data['Fossil Fuel Dependency'].toFixed(2)}%`}
          </p>
          {data.isBaseline && (
            <p style={{ fontSize: '0.85em', color: '#888', marginTop: '4px' }}>
              (ML-based baseline)
            </p>
          )}
        </div>
      );
    }
    return null;
  };

  return (
    <div className="chart-container">
      <h3 className="chart-title">
        Projected Fossil Fuel Dependency (ML-based baseline + policy simulation)
      </h3>
      <div style={{ marginBottom: '10px', fontSize: '0.9em', color: '#888' }}>
        <p style={{ margin: '4px 0' }}>
          • Baseline predictions are ML-learned from historical energy mix trends
        </p>
        <p style={{ margin: '4px 0' }}>
          • Future projections include policy-adjusted transition rates
        </p>
      </div>
      <ResponsiveContainer width="100%" height={350}>
        <LineChart data={chartData} margin={{ top: 5, right: 30, left: 20, bottom: 5 }}>
          <CartesianGrid strokeDasharray="3 3" stroke="#333" />
          <XAxis 
            dataKey="year" 
            stroke="#00FF6F"
            label={{ value: 'Year', position: 'insideBottom', offset: -5, fill: '#00FF6F' }}
          />
          <YAxis 
            stroke="#00FF6F"
            label={{ value: 'Fossil Fuel Dependency (%)', angle: -90, position: 'insideLeft', fill: '#00FF6F' }}
            domain={[0, 100]}
          />
          <Tooltip content={<CustomTooltip />} />
          <Legend />
          <Line
            type="monotone"
            dataKey="Fossil Fuel Dependency"
            stroke="#FF6B6B"
            strokeWidth={2}
            dot={{ fill: '#FF6B6B', r: 4 }}
            activeDot={{ r: 6 }}
          />
        </LineChart>
      </ResponsiveContainer>
    </div>
  );
}

export default FossilFuelTimeSeriesChart;
